import React from 'react';
import classNames from 'classnames';
import { SectionSplitProps } from '../../utils/SectionProps';
import SectionHeader2 from './partials/SectionHeader2';
import Image from '../elements/Image';
import Button from '../elements/Button';
import ButtonGroup from '../elements/ButtonGroup';
import { useState } from 'react';
import Modal from '../elements/Modal';
import ContactForm from '../elements/ContactUs';
import history from '../../history';

const propTypes = {
  ...SectionSplitProps.types
}

const defaultProps = {
  ...SectionSplitProps.defaults
}

const innerClasses = classNames(
'hero-inner section-inner',
);

const Blog = ({
  className,
  topOuterDivider,
  bottomOuterDivider,
  topDivider,
  bottomDivider,
  hasBgColor,
  invertColor,
  invertMobile,
  invertDesktop,
  alignTop,
  imageFill,
  ...props
}) => {

  const [videoModalActive, setVideomodalactive] = useState(false);
  const [pictureActive, setPictureActive] = useState(false);
  const showContactForm = useState(false)

  const openModal = (e) => {
    e.preventDefault();
    setVideomodalactive(true);
  }

  const openPicture = (e) => {
    e.preventDefault();
    setPictureActive(true);
  }

  const closeModal = (e) => {
    e.preventDefault();
    setVideomodalactive(false);
  }

  const closePicture = (e) => {
    e.preventDefault();
    setPictureActive(false);
  }

  const outerClasses = classNames(
    'features-split section',
    topOuterDivider && 'has-top-divider',
    bottomOuterDivider && 'has-bottom-divider',
    hasBgColor && 'has-bg-color',
    invertColor && 'invert-color',
    className
  );

  const innerClasses = classNames(
    'features-split-inner section-inner',
    topDivider && 'has-top-divider',
    bottomDivider && 'has-bottom-divider'
  );

  const splitClasses = classNames(
    'split-wrap',
    invertMobile && 'invert-mobile',
    invertDesktop && 'invert-desktop',
    alignTop && 'align-top'
  );

  return (
    <section
      {...props}
      id="resume"
      className={outerClasses}
      id="blog"
    >
      <div className="container">
        <div className={innerClasses}>
          <div className={splitClasses}>

            <div className="split-item">

              <a onClick={() => history.push('/Attribution')} style={{cursor:'pointer'}}>
              <div className="split-item-content reveal-from-left" data-reveal-container=".split-item">
                                <Image
                                  src={require('./../../assets/images/aness copy.png')}
                                  alt="Features split 03"
                                  style={{width: 400, height: 400, borderRadius: 400/ 2}} 
                                  resizeMode={"cover"} />
                                  <br></br>
                <h4 className="mt-0 ml-64 center-content-mobile " style={{color:'black', fontSize:"max(27px,1.5vw)"}}>
                  All in one 🔥
                  </h4>
                <p className="m-0 " style={{color:'grey',fontSize:"max(15px,1vw)", fontWeight: 'normal'}}>
                Fundamentals and Economics (Cash Flows, Balance Sheet, Earnings)
                   Technical Analysis (patterns and volumes driving offer/demand)
                  CEO and company prospects
                   Competition and industry prospects
                  Macroeconomics and opportunity costs introduced by other asset classes (rates, commodities, cryptos...)

                  </p>
              </div>
              </a>


              <div className="split-item-content reveal-from-left center-content-mobile" data-reveal-container=".split-item">
                                <Image
                                  src={require('./../../assets/images/social.png')}
                                  alt="Features split 03"
                                  width={200}
                                  height={200} />
                <h4 className="mt-0 ml-64 center-content-mobile " style={{color:'black', fontSize:"max(27px,1.5vw)"}}>
                  All in one 🔥
                  </h4>
                <p className="m-0 center-content-mobile" style={{color:'grey',fontSize:"max(15px,1vw)", fontWeight: 'normal'}}>
                Fundamentals and Economics (Cash Flows, Balance Sheet, Earnings)
                   Technical Analysis (patterns and volumes driving offer/demand)
                  CEO and company prospects
                   Competition and industry prospects
                  Macroeconomics and opportunity costs introduced by other asset classes (rates, commodities, cryptos...)

                  </p>
              </div>


            </div>


          </div>
        </div>
      </div>
                           <div className="container-sm">

      <center>

        <Button color="cyan" type="button" onClick={openModal}>
          <span className="btn-inner--icon mr-1" >
            <i className="ni ni-cloud-download-95" />
          </span>
          <p style={{fontFamily:"Arial"}}>SUBSCRIBE TO BETA</p>
          </Button>
      </center>


      <div className="reveal-from-bottom" data-reveal-delay="1600">

            </div>
        <Modal
          id="video-modal"
          show={videoModalActive}
          handleClose={closeModal}
          form=<ContactForm/>
          videoTag="iframe" />
        <Modal
          id="video-modal"
          show={pictureActive}
          handleClose={closePicture}
          form=<ContactForm/>
          videoTag="iframe" />



</div>
    <br></br>
    <br></br>
    </section>

  );
}

Blog.propTypes = propTypes;
Blog.defaultProps = defaultProps;

export default Blog;
