import { useEffect } from 'react';
import { withRouter } from 'react-router-dom';
import { scroller } from "react-scroll";

const ScrollToTop = () => {
    scroller.scrollTo("", {
      duration: 0,
      delay: 0,
      smooth: "easeInOutQuart",
    });
  };


export default withRouter(ScrollToTop);
